package dataTypeStudy2;

public class NumberWithDecimal {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        // float,double in use                     // syntax for float & double is differ then other data types (num+f)(num+d)		
		
		float num;                                            // variable declaration 
		num=1234654654.12f;                       // the o/p value is always in rounding method 
		
	    System.out.println("number is "+num);
	    
	    double num1;                                          //variable initialization
	    num1=123.123456789d;                     // double is use for precision value means after decimal value more precise for what exactly you are asking 
	    
	    System.out.println("number is "+num1);                //usage 
	    
	    
	}

}
